package iut.sae.algo;

import org.junit.Test;
import junit.framework.TestCase;


public class Efficacite12Test extends TestCase{

/*
 * RLE("")=""
 * RLE("abc")="1a1b1c"
 * RLE("abbccc")="1a2b3c"
 * RLE("aaabaa")="3a1b2a"
 * RLE("aAa")="1a1A1a"
 */
   @Test
   public void testRLE(){
      assertEquals("", efficacite12.RLE("") );
      
      assertEquals("1a2b3c", efficacite12.RLE("abbccc"));
      assertEquals("3a1b2a", efficacite12.RLE("aaabaa"));
      assertEquals("1a1A1a", efficacite12.RLE("aAa"));
      assertEquals("9W4W", efficacite12.RLE("WWWWWWWWWWWWW"));

   }


     /*
     * RLE(str, 1)=RLE(str)
     * RLE(str, 3)=RLE(RLE(RLE(str)))
     */
    @Test
    public void testRLERecursif(){
        try{
            assertEquals("", efficacite12.RLE_Recursif("", 1));
            assertEquals("", efficacite12.RLE_Recursif("", 3));

            assertEquals("1a1b1c", efficacite12.RLE_Recursif("abc", 1));
            assertEquals("1a2b3c", efficacite12.RLE_Recursif("abbccc", 1));
            assertEquals("3a1b2a", efficacite12.RLE_Recursif("aaabaa", 1));
            assertEquals("1a1A1a", efficacite12.RLE_Recursif("aAa", 1));

            assertEquals("111a111b111c", (efficacite12.RLE_Recursif("abc", 2))); // FAILED BY : efficacite04;
            assertEquals("311a311b311c", (efficacite12.RLE_Recursif("abc", 3))); // FAILED BY : efficacite04;

            String saeIte20="_";
             assertEquals(saeIte20, efficacite12.RLE_Recursif("SAE_Algo", 20)); // FAILED BY : efficacite04;
          }
          catch(Exception e){
             fail("Exception inatendue");
          }
       }

   /*
  * unRLE(RLE(str))=str
  *
  * unRLE("")=""
  * unRLE("1a1b1c")="abc"
  * unRLE("1a2b3c")="abbccc"
  * unRLE("3a1b2a")="aaabaa"
  * unRLE("1a1A1a")="aAa"
  */

   @Test
   public void testUnRLE(){
      try{
         assertEquals("", efficacite12.unRLE(""));
         assertEquals("abc", efficacite12.unRLE("1a1b1c"));
         assertEquals("abbccc", efficacite12.unRLE("1a2b3c"));
         assertEquals("aaabaa", efficacite12.unRLE("3a1b2a"));
         assertEquals("aAa", efficacite12.unRLE("1a1A1a"));
         assertEquals("WWWWWWWWWWWWW", efficacite12.unRLE("9W4W"));
      }
      catch(Exception e){
         fail("Exception inatendue");
      }
   }
   
   @Test
   public void testUnRLERecursif(){
      try{
         assertEquals("", efficacite12.unRLE_Recursif("", 1));
         assertEquals("", efficacite12.unRLE_Recursif("", 3));
         
         assertEquals("abc", efficacite12.unRLE_Recursif("1a1b1c", 1));
         assertEquals("abbccc", efficacite12.unRLE_Recursif("1a2b3c", 1));
         assertEquals("aaabaa", efficacite12.unRLE_Recursif("3a1b2a", 1));
         assertEquals("aAa", efficacite12.unRLE_Recursif("1a1A1a", 1));

         assertEquals("abc", (efficacite12.unRLE_Recursif("111a111b111c", 2))); // Exception raised : efficacite57;
         assertEquals("abc", (efficacite12.unRLE_Recursif("311a311b311c", 3))); // Exception raised : efficacite57;
      } catch(Exception e){
         fail("Exception inatendue");
      }
   }

}

/** This part will be for the number of fails/exceptions per class "efficacite" : 
 *  - efficacite04 : 3 fails             — respect des noms de fonctions
 *  - efficacite12 : 0                   — non respect des noms de fonctions (unRLE_Recursif, RLE_Recursif)
 *  - efficacite44 : 0                   — respect des noms de fonctions
 *  - efficacite46 : 0                   — respect des noms de fonctions
 *  - efficacite57 : 2 exceptions raised — respect des noms de fonctions
 */